/* * @lc app=leetcode id=66 lang=cpp * https://leetcode.com/problems/plus-one/description/ * * [66] Plus One */#include <vector>using namespace std;class Solution {public:    vector<int> plusOne(vector<int>& digits) {        //Runtime: 4 ms, faster than 65.94% of C++ online submissions for Plus One.
        //Memory Usage : 8.6 MB, less than 91.80% of C++ online submissions for Plus One.        add(digits, 1);        return digits;    }    void add(vector<int>& digits, int digit)    {        int c = digit;        for (auto it = digits.rbegin(); it != digits.rend(); ++it)        {            *it += c;            c = *it / 10;            *it %= 10;        }        if (c > 0)            digits.insert(digits.begin(), 1);    }};